import React from "react";

export default function Query(props) {
  const [studentIdQuery, setStudentIdQuery] = React.useState(1);

  React.useEffect(() => {
    fetch("http://localhost:8080/student/" + studentIdQuery)
      .then((response) => response.json())
      .then((data) => setStudentIdQuery(data));
  }, []);

  const studentArray = props.students;

  function searchStudent(event) {
    event.preventDefault();
    for (let i = 0; i < studentArray.length; i++) {
      if (studentArray[i].studentId === studentIdQuery) {
        //return <p>{studentArray[i].firstName}</p>;
        console.log(studentArray[i].firstName);
      }
    }
  }

  function handleChange(event) {
    const { value } = event.target;
    setStudentIdQuery(value);
  }

  return (
    <div>
      <h3>Search a Student</h3>
      <form onSubmit={searchStudent}>
        <input type="text" placeholder="Student ID" onChange={handleChange} />
        <button>Search</button>
      </form>
    </div>
  );
}
