import React from "react";
//Tratar de crear una funci贸n desde App que me permita cambiar el estado del id que quiero buscar, por medio de un onChange
//implementado en esta funci贸n y que reciba una callback function como prop, para poder actualizar el estado en la clase App
export default function Query(props) {
  //   React.useEffect(() => {
  //     fetch("http://localhost:8080/student/" + studentIdQuery)
  //       .then((response) => response.json())
  //       .then((data) => setStudentIdQuery(data.studentId));
  //   }, []);

  console.log(props);

  const studentArray = props.students;

  function searchStudent(event) {
    event.preventDefault();
    for (let i = 0; i < studentArray.length; i++) {
      if (studentArray[i].studentId == props.studentId) {
        console.log(studentArray[i].firstName);
      }
    }
  }

  return (
    <div>
      <h3>Search a Student</h3>
      <form onSubmit={searchStudent}>
        <input
          type="text"
          placeholder="Student ID"
          onChange={props.handleChange}
        />
        <button>Search</button>
      </form>
    </div>
  );
}
