import React from "react";
import { useNavigate, useParams } from "react-router-dom";
import StudentService from "../services/StudentService";
import CourseNavbar from "./CourseNavbar";

const CourseEnrollment = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [studentList, setStudentList] = React.useState([]);

  const [completeStudentList, setCompleteStudentList] = React.useState([]);

  const [searchTerm, setSearchTerm] = React.useState("");

  React.useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await StudentService.getStudentByCourseId(id);
        setStudentList(response.data);
        const response2 = await StudentService.getStudents;
        setCompleteStudentList(response2.data);
      } catch (error) {
        console.log(error);
      }
    };
    fetchData();
  }, []);

  const studentArr = studentList.map((student) => {
    return (
      <li key={student.studentId}>
        {student.firstName + " " + student.lastName}
      </li>
    );
  });

  const handleChange = (event) => {
    const { value } = event.target;
    setSearchTerm(value);
  };

  console.log(searchTerm);

  return (
    <div>
      <CourseNavbar />
      <h3>Enrolled Students</h3>
      <ol>{studentArr}</ol>
      <h3>Enroll Students</h3>
      <div>
        <input
          className="input--search"
          type="text"
          placeholder="Search by Name"
          onChange={handleChange}
        />
      </div>
      <div className="button--enroll">
        <button onClick={() => navigate("/courses")} className="rounded">
          Enroll
        </button>
      </div>
      <div>
        <button onClick={() => navigate("/courses")} className="rounded">
          Back
        </button>
      </div>
    </div>
  );
};

export default CourseEnrollment;
