import React from "react";

export default function Query(props) {
  const { studentId, handleChange } = props;
  const [studentName, setStudentName] = React.useState("");
  const [searchedStudentId, setSearchedStudentId] = React.useState();

  async function searchStudent() {
    const data = await fetch(`http://localhost:8080/student/${studentId}`)
      .then((response) => response.json())
      .catch(() => {
        return null;
      });
    if (!data) {
      setStudentName("");
      alert("Student not found");
    }
    console.log("data");
    console.log(data);
    if (data.studentId.toString() === studentId.toString()) {
      setStudentName(data.firstName);
      setSearchedStudentId(data.studentId);
    }
  }

  return (
    <div>
      <h3>Search a Student</h3>
      <input type="text" placeholder="Student ID" onChange={handleChange} />
      <button onClick={searchStudent}>Search</button>
      {studentName && <p>{studentName}</p>}
    </div>
  );
}
