import React from "react";
import Select from "react-select";
//Crear un botón aparte para inscribir estudiantes
const CourseForm = ({ departments }) => {
  const [course, setCourse] = React.useState({
    courseId: "",
    courseName: "",
    department: "",
    students: "",
  });

  const [depValue, setDepValue] = React.useState("");

  const departmentsData = departments.map((department) => {
    return {
      label: "department",
      value: department.depName,
    };
  });

  const handleSelectChange = (event) => {
    const { label, value } = event;
    setCourse((prevCourse) => ({
      ...prevCourse,
      [label]: value,
    }));
    //setDepValue(value);
  };

  function handleChange(event) {
    console.log(event);
    const { name, value } = event.target;
    setCourse((prevCourse) => ({
      ...prevCourse,
      [name]: value,
    }));
  }

  const saveCourse = () => {
    console.log("saved");
  };

  console.log(course);

  return (
    <div className="flex max-w-2xl mx-auto shadow border-b">
      <h3>Course Form</h3>
      <div className="px-8 py-8">
        <div className="font-thin text-2xl tracking-wider">
          <h4>Add New Course</h4>
        </div>
        <div className="input--form">
          <label className="label--form">Course Name:</label>
          <input
            type="text"
            name="courseName"
            value={course.courseName}
            onChange={(e) => handleChange(e)}
          ></input>
        </div>
        <div className="input--form">
          <label className="label--form">Department:</label>
          {/* <input
            type="text"
            name="department"
            value={course.department}
            onChange={(e) => handleChange(e)}
          ></input> */}
          <Select
            options={departmentsData}
            name="department"
            value={depValue}
            onChange={handleSelectChange}
          />
        </div>
        <div className="items-center justify-center h-14 w-full my-4 space-x-4 pt-4">
          <button onClick={saveCourse} className="rounded">
            Add
          </button>
        </div>
      </div>
    </div>
  );
};

export default CourseForm;
