import React from "react";
import { useNavigate, useParams } from "react-router-dom";
import CourseService from "../services/CourseService";
import StudentService from "../services/StudentService";
import CourseNavbar from "./CourseNavbar";

const CourseEnrollment = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [studentList, setStudentList] = React.useState([]);

  const [completeStudentList, setCompleteStudentList] = React.useState([]);

  const [searchTerm, setSearchTerm] = React.useState("");

  React.useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await StudentService.getStudentByCourseId(id);
        const responseAll = await StudentService.getStudentNotEnrolled(id);
        setStudentList(response.data);
        setCompleteStudentList(responseAll.data);
      } catch (error) {
        console.log(error);
      }
    };
    fetchData();
    console.log(completeStudentList);
  }, []);

  const studentArr = studentList.map((student) => {
    return (
      <li key={student.studentId}>
        {student.firstName + " " + student.lastName}
      </li>
    );
  });

  const handleChange = (event) => {
    const { value } = event.target;
    setSearchTerm(value);
  };

  const enrollStudent = () => {
    CourseService.enrollStudent(id, {
      studentId: 5,
      firstName: "David",
      lastName: "Alfaro",
    })
      .then((response) => {
        console.log(response);
        window.location.reload(false);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  return (
    <div>
      <CourseNavbar />
      <h3>Enrolled Students</h3>
      <ol>{studentArr}</ol>
      <h3>Enroll Students</h3>
      {/* <div>
        <input
          className="input--search"
          type="text"
          placeholder="Search by Name"
          onChange={handleChange}
        />
      </div> */}
      <div>
        {completeStudentList.map((student) => {
          return (
            <div>
              <p>{student.firstName + " " + student.lastName}</p>
              <button onClick={enrollStudent} className="rounded--enroll">
                Enroll
              </button>
            </div>
          );
        })}
      </div>
      {/* <div className="button--enroll">
        <button onClick={() => navigate("/courses")} className="rounded">
          Enroll
        </button>
      </div> */}
      <div>
        <button onClick={() => navigate("/courses")} className="rounded--back">
          Back
        </button>
      </div>
    </div>
  );
};

export default CourseEnrollment;
