import React from "react";

export default function Query(props) {
  const studentArray = props.students;

  function searchStudent(event) {
    event.preventDefault();
    for (let i = 0; i < studentArray.length; i++) {
      if (studentArray[i].studentId == props.studentId) {
        //return <p>{studentArray[i].firstName}</p>;
        console.log(studentArray[i].firstName);
      }
    }
  }

  function wasFound() {
    const value = searchStudent();
    return value ? true : false;
  }

  return (
    <div>
      <h3>Search a Student</h3>

      <input
        type="text"
        placeholder="Student ID"
        onChange={props.handleChange}
      />
      {/* <button onClick={searchStudent}>Search</button> */}
      {<button onClick={searchStudent}>Search</button> && <p>jiji</p>}
    </div>
  );
}
