import React from "react";

export default function Query(props) {
  const { studentArray, studentId } = props;
  const [studentName, setStudentName] = React.useState("");

  function searchStudent() {
    for (let i = 0; i < studentArray.length; i++) {
      if (studentArray[i].studentId.toString() === studentId.toString()) {
        console.log("trueeeee", studentArray[i].firstName);
        setStudentName(studentArray[i].firstName);
      } else {
        setStudentName("not found");
      }
      console.log(studentArray[i].studentId, studentId, studentName);
    }
  }

  return (
    <div>
      <h3>Search a Student</h3>
      <input
        type="text"
        placeholder="Student ID"
        onChange={props.handleChange}
      />
      <button onClick={searchStudent}>Search</button>
      {studentName && <p>{studentName}</p>}
      {/* <p>{!studentName == "" ? studentName : ""}</p> */}
    </div>
  );
}
